import React from 'react';
import Header from './header';
import Footer from './Footer';
import data from './yourdata';
import "../App.css";
import { Link } from "react-router-dom";
import { Form } from "react-bootstrap";
import { Button } from "react-bootstrap";
// import ImageUploader from 'react-images-upload';
import ImageUploading from "react-images-uploading";


class CampaignDetails extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            location: '',
            email: "",
            phone: "",
            image: ""
        };
    }

    updateLocation = (result) => {
        this.setState({
            location: result
        })
    }

    updateEmail = (result) => {
        this.setState({
            email: result
        })
    }

    updateNumber = (result) => {
        this.setState({
            number: result
        })
    }

    uploadInfo = () => {
        localStorage.setItem("Location", this.state.location)
        localStorage.setItem("Email", this.state.email)
        localStorage.setItem("Number", this.state.Number)
    }

    render() {
        return (
            <div>
            <Header name={data.name} contactEmail={data.contactEmail}></Header>
            <div className="about_container">
                <h1>Add Details</h1>
                <p>* required information</p>
                <h2>
                    More information is always better
                </h2>
                <h1>Location</h1>
                <LocationForm onChange={(result) => {this.updateLocation(result)}}></LocationForm>
                <br/>

                <h1>Contact Information</h1>
                <EmailForm onChange={(result) => {this.updateEmail(result)}}></EmailForm>
                <br/>
                <PhoneForm onChange={(result) => {this.updateNumber(result)}}></PhoneForm>

                <h1>Add Photos</h1>
                <PhotoUpload></PhotoUpload>
                <br/>
                <br/>
                <Submission db = {this.props.db}
                            onSubmit = {() => {this.uploadInfo()}}></Submission>
            </div>

            <Footer
                contactEmail={data.contactEmail}
                socialLinks={data.social}
            ></Footer>
        </div>
        )
    }
}


