class CampaignBrowsing extends Component {
    render() {
        select(this.props.db)
        return (
            <div>
                <Header name={data.name} contactEmail={data.contactEmail}></Header>
                <Row>
                    <Col md={9}>
                        <h4 style={{ textAlign: "center", marginBottom: "40px", marginLeft: "280px" }}>Browse Campaigns</h4>
                    </Col>
                    <Col md={2}>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                Type of Campaign
                        </Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item >
                                    <DropClick text = "Supplies"></DropClick>
                                </Dropdown.Item>
                                <Dropdown.Item>Money</Dropdown.Item>
                                <Dropdown.Item>Human Resources</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </Col>
                    <Col md={1}>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                Sort By
                        </Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item >Date Posted</Dropdown.Item>
                                <Dropdown.Item>A-Z</Dropdown.Item>
                                <Dropdown.Item>Z-A</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </Col>
                </Row>
                <Row>
                    <CardsList db = {this.props.db} ></CardsList> 
                    {/* <Card
                        title="Toy Drive for Seattle Childrens Hospital"
                        content={
                            <div>
                                <img src={background}></img>
                                <div className="description">
                                    <p>This is the description</p>
                                </div>
                                <div className="learn-more">
                                    <Link to='/campaign_information'>
                                        <a>Learn More</a>
                                    </Link>
                                </div>
                            </div>
                        }
                    />
                    <Card
                        title="Toy Drive for Seattle Childrens Hospital"
                        content={
                            <div>
                                <img src={background}></img>
                                <div className="description">
                                    <p>This is the description</p>
                                </div>
                                <div className="learn-more">
                                    <Link to='/campaign_information'>
                                        <a>Learn More</a>
                                    </Link>
                                </div>
                            </div>
                        }
                    />
                    <Card
                        title="Toy Drive for Seattle Childrens Hospital"
                        content={
                            <div>
                                <img src={background}></img>
                                <div className="description">
                                    <p>This is the description</p>
                                </div>
                                <div className="learn-more">
                                    <Link to='/campaign_information'>
                                        <a>Learn More</a>
                                    </Link>
                                </div>
                            </div>
                        }
                    /> */}
                </Row>
                <Footer contactEmail={data.contactEmail} socialLinks={data.social}></Footer>
            </div>
        )
    }
}

class DropClick extends React.Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        var cards = JSON.parse(localStorage.getItem("cards"))
        var new_cards = cards.map(d => d.Type === this.props.text)
        console.log(cards)
        console.log(new_cards)
    }

    render() {
        return (
            <span onClick= {(e) => this.handleSubmit()}>
                {this.props.text}
            </span>
        )
    }
}

class CardsList extends React.Component {
    render() {
        var elements = []
        var mydb = this.props.db

        var element_object = JSON.parse(localStorage.getItem("cards"))
        var i
        for (i = 0; i < element_object.length; i++) {
            var object = element_object[i]
            elements.push(<Card 
                title = {object.Title} 
                content={
                    <div>
                        <img src={object.Picture} alt="purpose of campaign"></img>
                        <div className="description">
                            <p>{object.Purpose}</p>
                        </div>
                        <div className="learn-more">
                            <ToDetail index = {object.index}></ToDetail>
                        </div>
                    </div>
                } 
            />)
        }
        
        return (
            <div>
                {elements}
            </div>
        )
    }

}

class ToDetail extends React.Component {
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        localStorage.setItem("index", this.props.index);
    }

    render() {
        return (
            <Link to='/campaign_information'
                  onClick= {(e) => this.handleSubmit()}>
                <a>Learn More</a>
            </Link>
        )
    }
}

function select(mydb) {
    mydb.transaction(function (t) {
        t.executeSql("SELECT * FROM campaigns", [], function (t, results){ 
            var i
            var temp = []
            for (i = 0; i < results.rows.length; i++) {
                var row = results.rows.item(i);
                temp.push({
                    Title: row.Title,
                    Picture: row.Picture,
                    Purpose: row.Purpose,
                    Type: row.Type,
                    Phone: row.Phone,
                    Location: row.Location,
                    Email: row.Email,
                    Number: row.Number,
                    Date: row.Date,
                    index: i  
                })
            }
            localStorage.setItem("cards", JSON.stringify(temp))
        });
    });
}



export default CampaignBrowsing;
