class CampaignBrowsing extends Component {
    render() {
        
        return (
            <div>
                <Header name={data.name} contactEmail={data.contactEmail}></Header>
                <Row>
                    <Col md={9}>
                        <h4 style={{ textAlign: "center", marginBottom: "40px", marginLeft: "280px" }}>Browse Campaigns</h4>
                    </Col>
                    <Col md={2}>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                Type of Campaign
                        </Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item >Supplies</Dropdown.Item>
                                <Dropdown.Item>Money</Dropdown.Item>
                                <Dropdown.Item>Human Resources</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </Col>
                    <Col md={1}>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                Sort By
                        </Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item >Date Posted</Dropdown.Item>
                                <Dropdown.Item>A-Z</Dropdown.Item>
                                <Dropdown.Item>Z-A</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </Col>
                </Row>
                <Row>
                    <CardsList db = {this.props.db}></CardsList> 
                    {/* <Card
                        title="Toy Drive for Seattle Childrens Hospital"
                        content={
                            <div>
                                <img src={background}></img>
                                <div className="description">
                                    <p>This is the description</p>
                                </div>
                                <div className="learn-more">
                                    <Link to='/campaign_information'>
                                        <a>Learn More</a>
                                    </Link>
                                </div>
                            </div>
                        }
                    />
                    <Card
                        title="Toy Drive for Seattle Childrens Hospital"
                        content={
                            <div>
                                <img src={background}></img>
                                <div className="description">
                                    <p>This is the description</p>
                                </div>
                                <div className="learn-more">
                                    <Link to='/campaign_information'>
                                        <a>Learn More</a>
                                    </Link>
                                </div>
                            </div>
                        }
                    />
                    <Card
                        title="Toy Drive for Seattle Childrens Hospital"
                        content={
                            <div>
                                <img src={background}></img>
                                <div className="description">
                                    <p>This is the description</p>
                                </div>
                                <div className="learn-more">
                                    <Link to='/campaign_information'>
                                        <a>Learn More</a>
                                    </Link>
                                </div>
                            </div>
                        }
                    /> */}
                </Row>
                <Footer contactEmail={data.contactEmail} socialLinks={data.social}></Footer>
            </div>
        )
    }
}

class CardsList extends React.Component {
    render() {
        var elements = []
        this.props.db.transaction(function (t) {
            t.executeSql("SELECT * FROM campaigns", [], function (t, results){ 
                var i
                for (i = 0; i < results.rows.length; i++) {
                    var row = results.rows.item(i);
                    console.log(row.Purpose)
                    elements.push(<Card 
                        title = {row.Title} 
                        content={
                            <div>
                                <img src={row.Picture} alt="purpose of campaign"></img>
                                <div className="description">
                                    <p>{row.Purpose}</p>
                                </div>
                                <div className="learn-more">
                                    <Link to='/campaign_information'>
                                        <a>Learn More</a>
                                    </Link>
                                </div>
                            </div>
                        } 
                    />)
                }
                console.log(elements)
                localStorage.setItem("cards", JSON.stringify(elements))
            });
        });
        console.log(JSON.parse(localStorage.getItem("cards")))
        return (
            <div>
                {elements}
            </div>
        )
    }

}

function outputCampaigns(mydb) {

    mydb.transaction(function (t) {
        t.executeSql("SELECT * FROM campaigns", [], function (t, data){ 
            updateCampaignList(data)
        });
    });
}

function updateCampaignList(results) {
    var elements_temp = []
    var i
    for (i = 0; i < results.rows.length; i++) {
        var row = results.rows.item(i);
        console.log(i)
        elements_temp.push(<Card 
            title = {row.title} 
            content={
                <div>
                    <img src={row.Picture} alt="purpose of campaign"></img>
                    <div className="description">
                        <p>{row.Purpose}</p>
                    </div>
                    <div className="learn-more">
                        <Link to='/campaign_information'>
                            <a>Learn More</a>
                        </Link>
                    </div>
                </div>
            } 
        />)
    }
    return elements_temp
}



export default CampaignBrowsing;
