import React from 'react';
import Header from './header';
import Footer from './Footer';
import data from './yourdata';
import "../App.css";
import { Link } from "react-router-dom";
import { Form } from "react-bootstrap";
import { Button } from "react-bootstrap";
import ImageUploader from 'react-images-upload';


const CampaignDetails = ()=>{
    return (
        <div>
        <Header name={data.name} contactEmail={data.contactEmail}></Header>
        <div className="about_container">
            <h1>Add Details</h1>
            <p>* required information</p>
            <h2>
                More information is always better
            </h2>
            <h1>Location</h1>
            <LocationForm>Seattle, WA</LocationForm>
            <br/>

            <h1>Contact Information</h1>
            <EmailForm>email</EmailForm>
            <PhoneForm>phone number</PhoneForm>

            <h1>Add Photos</h1>
            <PhotoUpload></PhotoUpload>
            <br/>
            <br/>
            <Link to="/landing_page">
                    <Button
                        id="finish_button"
                        variant="primary"
                        type="submit"
                    >
                        Finish Creation
                    </Button>
            </Link>
        </div>

        <Footer
            contactEmail={data.contactEmail}
            socialLinks={data.social}
        ></Footer>
    </div>
    )
}


class LocationForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: "" };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleSubmit(event) {
        alert('Location was submitted: ' + this.state.value);
        localStorage.setItem("Location", this.state.value);
        // event.preventDefault();
    }

    render() {
        return (
            <Form>
                {" "}
                onSubmit={this.handleSubmit}
                <Form.Control
                    type="text"
                    placeholder="Location goes here"
                    value={this.state.value}
                    onChange={this.handleChange}
                />

                <Button
                    id="next_button"
                    variant="primary"
                    type="submit"
                    onClick={(e) => this.handleSubmit()}>
                    Submit
                </Button>
            </Form>
        );
    }
}

class EmailForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: "" };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleSubmit(event) {
        alert('Email was submitted: ' + this.state.value);
        localStorage.setItem("Email", this.state.value);
        // event.preventDefault();
    }

    render() {
        return (
            <Form>
                {" "}
                onSubmit={this.handleSubmit}
                <Form.Control
                    type="text"
                    placeholder="Email goes here"
                    value={this.state.value}
                    onChange={this.handleChange}
                />

                <Button
                    id="next_button"
                    variant="primary"
                    type="submit"
                    onClick={(e) => this.handleSubmit()}>
                    Submit
                </Button>
            </Form>
        );
    }
}

class PhoneForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: "" };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleSubmit(event) {
        alert('Phone number was submitted: ' + this.state.value);
        localStorage.setItem("Phone", this.state.value);
        // event.preventDefault();
    }

    render() {
        return (
            <Form>
                {" "}
                onSubmit={this.handleSubmit}
                <Form.Control
                    type="text"
                    placeholder="Phone number goes here"
                    value={this.state.value}
                    onChange={this.handleChange}
                />

                <Button
                    id="next_button"
                    variant="primary"
                    type="submit"
                    onClick={(e) => this.handleSubmit()}>
                    Submit
                </Button>
            </Form>
        );
    }
}

class PhotoUpload extends React.Component {
 
    constructor(props) {
        super(props);
         this.state = { pictures: [],
                        file: "" };
         this.onDrop = this.onDrop.bind(this);
    }

    onDrop(picture) {
        this.setState({
            pictures: this.state.pictures.concat(picture),
            file: URL.createObjectURL(this.state.picture[0])
        });
        // var pic = this.state.pictures 
        // var imgCanvas = document.createElement("canvas")
        // var imgContext = imgCanvas.getContext("2d")

        // imgContext.drawImage(pic, 0, 0) 
        // var storageFiles = {}
        // storageFiles.pic = imgCanvas.toDataURL("image/png")

        // localStorage.setItem("picture", JSON.stringify(storageFiles));
        console.log(this.state.pictures)
    }
 
    render() {
        return (
            <div>
            <ImageUploader
                withIcon={true}
                buttonText='Choose images'
                onChange={this.onDrop}
                imgExtension={['.jpg', '.gif', '.png', '.gif']}
                maxFileSize={5242880}
            />
            <img src={this.state.file} alt=".."></img>
            </div>
        );
    }
}

export default CampaignDetails
