class PhotoUpload extends React.Component {
    onChange = (imageList) => {
      // data for submit
      console.log(imageList);
      localStorage.setItem("Picture", imageList[0].dataURL);
    };
  
    render() {
      return (
        <ImageUploading
          onChange={this.onChange}
          maxNumber={10}
          multiple
          maxFileSize={5 * 1024 * 1024}
          acceptType={["jpg", "gif", "png"]}
        >
          {({ imageList, onImageUpload, onImageRemoveAll }) => (
            // write your building UI
            <div>
              <button onClick={onImageUpload}>Upload images</button>
              <button onClick={onImageRemoveAll}>Remove all images</button>
  
              {imageList.map((image) => (
                <div key={image.key}>
                  <img src={image.dataURL} />
                  <button onClick={image.onUpdate}>Update</button>
                  <button onClick={image.onRemove}>Remove</button>
                </div>
              ))}
            </div>
          )}
        </ImageUploading>
      );
    }
 }

  class Submission extends React.Component {
    constructor(props) {
        super(props);

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        alert('Campaign successfully created!');
        console.log(this.props.db)
        addCampaign(this.props.db) 
        
    }

    render() {
        return (
            <Link to="/">
                    <Button
                        id="finish_button"
                        variant="primary"
                        type="submit"
                        onClick={(e) => this.handleSubmit()}
                    >
                        Finish Creation
                    </Button>
            </Link>
        );
    }
 }

 function addCampaign(mydb) {
    var title = localStorage.getItem("Title");
    var type = localStorage.getItem("Type");
    var phone = localStorage.getItem("phone");
    var purpose = localStorage.getItem("Purpose");
    var location = localStorage.getItem("Location");
    var picture = localStorage.getItem("Picture");
    var email = localStorage.getItem("Email");
    var number = localStorage.getItem("Number");

    var model = "Honda"
    var make = "Civic"
    mydb.transaction(function (t) {
        //t.executeSql("INSERT INTO campaigns (Title, Type, Phone, Purpose, Location, Picture, Email, Number) VALUES (?, ?, ?, ?, ?, ?, ?, ?)", [title, type, phone, purpose, location, picture, email, number]);
        t.executeSql("INSERT INTO cars (make, model) VALUES (?, ?)", [make, model]);
    });
}

export default CampaignDetails
