// import websql from "websql" 


 
export 
function startDB() {
    if (window.openDatabase) {
        var mydb = openDatabase("donaid_db", "1.0", "Database of campaign information",
                                    1024 * 1024);

        mydb.transaction(function (t) {
        t.executeSql("CREATE TABLE IF NOT EXISTS campaigns (id INTEGER PRIMARY KEY ASC, Title TEXT, Type TEXT, Phone Text, Purpose Text, Location Text, Picture Text, Email Text, Number Int)");
        });
    }
}

function addCampaign() {
    var title = localStorage.getItem("Title");
    var type = localStorage.getItem("Type");
    var phone = localStorage.getItem("phone");
    var purpose = localStorage.getItem("Purpose");
    var location = localStorage.getItem("Location");
    var picture = localStorage.getItem("Picture");
    var email = localStorage.getItem("Email");
    var number = localStorage.getItem("Number");
}
